mapper.select:
SELECT 

	a.id, 
	a.relevanceTable, 
	a.relevanceId, 
	a.userId, 
	a.userName, 
	a.contractId, 
	a.code, 
	a.fileName, 
	a.suffix, 
	a.savePathFile, 
	a.tag, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM attachment AS a



		AND a.id = #{id}


		AND a.relevanceTable = #{relevanceTable}


		AND a.relevanceId = #{relevanceId}


		AND a.userId = #{userId}


		AND a.userName = #{userName}


		AND a.contractId = #{contractId}


		AND a.code = #{code}


		AND a.fileName = #{fileName}


		AND a.suffix = #{suffix}


		AND a.savePathFile = #{savePathFile}


		AND a.tag = #{tag}


		AND a.addDate = #{addDate}


		AND a.addUser = #{addUser}


		AND a.addIp = #{addIp}


		AND a.modifiedDate = #{modifiedDate}


		AND a.modifiedUser = #{modifiedUser}


		AND a.modifiedIp = #{modifiedIp}



LIMIT 1

