SELECT 
	id, 
	department, 
	departmentId, 
	applyMonth, 
	leaderId, 
	leaderName, 
	addDate, 
	modifiedDate, 
	approveState, 
	userId, 
	flag, 
	pid, 
	userName
FROM depOffice


		AND id = #{id}


		AND department = #{department}


		AND departmentId = #{departmentId}


		AND applyMonth = #{applyMonth}


		AND leaderId = #{leaderId}


		AND leaderName = #{leaderName}


		AND addDate = #{addDate}


		AND modifiedDate = #{modifiedDate}


		AND approveState = #{approveState}


		AND userId = #{userId}


		AND flag = #{flag}


		AND pid = #{pid}


		AND userName = #{userName}


LIMIT 1

SELECT 
	id, 
	department, 
	departmentId, 
	applyMonth, 
	leaderId, 
	leaderName, 
	addDate, 
	modifiedDate, 
	approveState, 
	userId, 
	flag, 
	pid, 
	userName,
	DATE_FORMAT(modifiedDate, '%Y-%m-%d %H:%i') AS showModifiedDate
FROM depOffice
where 1=1

		AND id = #{id}


		AND department = #{department}


		AND departmentId = #{departmentId}


		AND applyMonth = #{applyMonth}


		AND leaderId = #{leaderId}


		AND leaderName = #{leaderName}


		AND addDate = #{addDate}


		AND modifiedDate = #{modifiedDate}


		AND approveState IN

		#{item}



		AND userId = #{userId}


		AND flag = #{flag}


		AND pid = #{pid}


		AND userName = #{userName}


		ORDER BY ${sort} ${order}

LIMIT ${startIndex}, ${rows}

