mapper.delete:
DELETE FROM email


		AND id = #{id}


		AND theme = #{theme}


		AND content = #{content}


		AND sender = #{sender}


		AND senderName = #{senderName}


		AND receiver = #{receiver}


		AND receiverName = #{receiverName}


		AND receiverEmail = #{receiverEmail}


		AND copyTo = #{copyTo}


		AND email = #{email}


		AND statusSend = #{statusSend}


		AND hostName = #{hostName}


		AND smtpPort = #{smtpPort}


		AND emailLogin = #{emailLogin}


		AND emailPwd = #{emailPwd}


		AND subject = #{subject}


		AND charset = #{charset}


		AND type = #{type}


		AND typeId = #{typeId}


		AND status = #{status}


		AND msg = #{msg}


		AND addDate = #{addDate}


		AND addUser = #{addUser}


		AND addIp = #{addIp}


		AND modifiedDate = #{modifiedDate}


		AND modifiedUser = #{modifiedUser}


		AND modifiedIp = #{modifiedIp}

mapper.select:
SELECT 

	a.id, 
	a.theme, 
	a.content, 
	a.sender, 
	a.senderName, 
	a.receiver, 
	a.receiverName, 
	a.receiverEmail, 
	a.copyTo, 
	a.email, 
	a.statusSend, 
	a.hostName, 
	a.smtpPort, 
	a.emailLogin, 
	a.emailPwd, 
	a.subject, 
	a.charset, 
	a.type, 
	a.typeId, 
	a.status, 
	a.msg, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM email AS a



		AND a.id = #{id}


		AND a.theme = #{theme}


		AND a.content = #{content}


		AND a.sender = #{sender}


		AND a.senderName = #{senderName}


		AND a.receiver = #{receiver}


		AND a.receiverName = #{receiverName}


		AND a.receiverEmail = #{receiverEmail}


		AND a.copyTo = #{copyTo}


		AND a.email = #{email}


		AND a.statusSend = #{statusSend}


		AND a.hostName = #{hostName}


		AND a.smtpPort = #{smtpPort}


		AND a.emailLogin = #{emailLogin}


		AND a.emailPwd = #{emailPwd}


		AND a.subject = #{subject}


		AND a.charset = #{charset}


		AND a.type = #{type}


		AND a.typeId = #{typeId}


		AND a.status = #{status}


		AND a.msg = #{msg}


		AND a.addDate = #{addDate}


		AND a.addUser = #{addUser}


		AND a.addIp = #{addIp}


		AND a.modifiedDate = #{modifiedDate}


		AND a.modifiedUser = #{modifiedUser}


		AND a.modifiedIp = #{modifiedIp}




	ORDER BY a.${sort} ${order}

LIMIT 1

