mapper.select:
SELECT 
	id, 
	code, 
	loginName, 
	displayName, 
	versionNO, 
	userIds, 
	type, 
	preCodeId, 
	codeId, 
	currentFlag, 
	addDate, 
	addIp, 
	addUser, 
	modifyDate, 
	modifyIp, 
	modifyUser
FROM history


		AND id = #{id}


		AND code = #{code}


		AND loginName = #{loginName}


		AND displayName = #{displayName}


		AND versionNO = #{versionNO}


		AND userIds = #{userIds}


		AND type = #{type}


		AND preCodeId = #{preCodeId}


		AND codeId = #{codeId}


		AND currentFlag = #{currentFlag}


		AND addDate = #{addDate}


		AND addIp = #{addIp}


		AND addUser = #{addUser}


		AND modifyDate = #{modifyDate}


		AND modifyIp = #{modifyIp}


		AND modifyUser = #{modifyUser}


LIMIT 1

SELECT 
	id, 
	code, 
	loginName, 
	displayName, 
	versionNO, 
	userIds, 
	type, 
	preCodeId, 
	codeId, 
	currentFlag, 
	addDate, 
	addIp, 
	addUser, 
	modifyDate, 
	modifyIp, 
	modifyUser
FROM history
where 1=1

		AND id = #{id}


		AND code = #{code}


		AND loginName = #{loginName}


		AND displayName = #{displayName}


		AND versionNO = #{versionNO}


		AND userIds = #{userIds,jdbcType=VARCHAR}


		AND type = #{type}


		AND preCodeId = #{preCodeId}


		AND codeId = #{codeId}


		AND currentFlag = #{currentFlag}


		AND addDate = #{addDate}


		AND addIp = #{addIp}


		AND addUser = #{addUser}


		AND modifyDate = #{modifyDate}


		AND modifyIp = #{modifyIp}


		AND modifyUser = #{modifyUser}


		ORDER BY ${sort} ${order}

SELECT COUNT(1) FROM history
where 1=1

		AND id = #{id}


		AND code = #{code}


		AND loginName = #{loginName}


		AND displayName = #{displayName}


		AND versionNO = #{versionNO}


		AND userIds = #{userIds}


