mapper.select:
SELECT 
	id, 
	remarks, 
	linkRemarks, 
	url, 
	addDate, 
	modifiedDate, 
	status
FROM information


		AND id = #{id}


		AND remarks = #{remarks}


		AND linkRemarks = #{linkRemarks}


		AND url = #{url}


		AND addDate = #{addDate}


		AND modifiedDate = #{modifiedDate}


		AND status = #{status}


LIMIT 1

SELECT 
	id, 
	remarks, 
	linkRemarks, 
	url, 
	addDate, 
	modifiedDate, 
	status
FROM information
where 1 = 1

		AND id = #{id}


		AND remarks = #{remarks}


		AND linkRemarks = #{linkRemarks}


		AND url = #{url}


		AND addDate = #{addDate}


		AND modifiedDate = #{modifiedDate}


		AND status = #{status}


		ORDER BY ${sort} ${order}

SELECT COUNT(1) FROM information
where 1 = 1

		AND id = #{id}


		AND remarks = #{remarks}


		AND linkRemarks = #{linkRemarks}


		AND url = #{url}


		AND addDate = #{addDate}


		AND modifiedDate = #{modifiedDate}


		AND status = #{status}
