mapper.delete:
DELETE FROM items


		AND contractId = #{contractId}


		AND id = #{id}


		AND budgetId = #{budgetId}


		AND tagItem = #{tagItem}

mapper.select:
SELECT id, contractId, commodity, specification, unit, quantity, unitPriceForeign, unitPrice, amount, modified, addUser, addDate, modifiedUser, modifiedDate, amountRMB, currencyId, currency, amountForeign, exchangeRate, tagItem, unitConsumption, rowNo, colourNumber, remark, articleNo, budgetId, operationDate, process, budgetType, deliveryDate FROM items AS a WHERE 1 = 1
AND a.id = #{id, jdbcType=BIGINT}
LIMIT 1

SELECT id, contractId, commodity, specification, unit, quantity, unitPriceForeign, unitPrice, amount, modified, addUser, addDate, modifiedUser, modifiedDate, amountRMB, currencyId, currency, amountForeign, exchangeRate, tagItem, unitConsumption, rowNo, colourNumber, remark, articleNo, budgetId, operationDate, process, budgetType, deliveryDate FROM items
AND contractId = #{contractId}
AND budgetId = #{budgetId}
AND budgetType = #{budgetType}
AND tagItem = #{tagItem}
ORDER BY ${sort} ${order}

SELECT COUNT(1)
FROM items AS a


		AND contractId = #{contractId}


		AND tagItem = #{tagItem}


		ORDER BY ${sort} ${order}
