mapper.select:
SELECT 
	id, 
	purchaseId, 
	comment, 
	createUserId, 
	createUserName, 
	createDatetime
FROM purchaseComment


		AND id = #{id}


		AND purchaseId = #{purchaseId}


		AND comment = #{comment}


		AND createUserId = #{createUserId}


		AND createUserName = #{createUserName}


		AND createDatetime = #{createDatetime}


LIMIT 1

SELECT 
	id, 
	purchaseId, 
	comment, 
	createUserId, 
	createUserName, 
	createDatetime
FROM purchaseComment


		AND id = #{id}


		AND purchaseId = #{purchaseId}


		AND comment = #{comment}


		AND createUserId = #{createUserId}


		AND createUserName = #{createUserName}


		AND createDatetime = #{createDatetime}

		ORDER BY createDatetime DESC


	LIMIT ${startIndex}, ${rows}

SELECT COUNT(1) FROM purchaseComment


		AND id = #{id}


		AND purchaseId = #{purchaseId}


		AND comment = #{comment}


		AND createUserId = #{createUserId}


		AND createUserName = #{createUserName}


		AND createDatetime = #{createDatetime}
