SELECT 
	id, 
	purchaseId, 
	category1Id, 
	category1Name, 
	category2Id, 
	category2Name, 
	description, 
	divisionId, 
	divisionName, 
	customerId, 
	customerName, 
	quoteAmount, 
	`index`, 
	isPurchased, 
	purchaseCompleteDate, 
	isReimbursed
FROM purchaseItem


		AND id = #{id}


		AND purchaseId = #{purchaseId}


		AND category1Id = #{category1Id}


		AND category1Name = #{category1Name}


		AND category2Id = #{category2Id}


		AND category2Name = #{category2Name}


		AND description = #{description}


		AND divisionId = #{divisionId}


		AND divisionName = #{divisionName}


		AND customerId = #{customerId}


		AND customerName = #{customerName}


		AND quoteAmount = #{quoteAmount}


		AND `index` = #{index}


		AND isPurchased = #{isPurchased}


		AND purchaseCompleteDate = #{purchaseCompleteDate}


		AND isReimbursed = #{isReimbursed}


LIMIT 1

SELECT pu.id AS purchase_id, pu.no AS purchase_no, pu.quoteCurrencyId AS purchase_quoteCurrencyId, pui.id, pui.category1Id, pui.category1Name, pui.category2Id, pui.category2Name, pui.purchaseId, pui.description, pui.divisionId, pui.divisionName, pui.customerId, pui.customerName, pui.quoteAmount, pui.`index`, pui.isPurchased, pui.purchaseCompleteDate, pui.isReimbursed FROM purchaseItem pui INNER JOIN (SELECT * FROM purchase WHERE status = 6) pu ON pu.id = pui.purchaseId WHERE 1 = 1 AND pui.isPurchased = 1
AND pui.isReimbursed = #{record.isReimbursed}
AND pu.no LIKE CONCAT('%', CONCAT(#{no}, '%'))
AND pu.createUserId = #{userId}
ORDER BY pui.purchaseCompleteDate DESC, pui.`index` ASC
LIMIT ${record.startIndex}, ${record.rows}

