mapper.select:
SELECT 

	a.id, 
	a.roleId, 
	a.code, 
	a.parentCode, 
	a.type, 
	a.typeId, 
	a.typeSys, 
	a.haveExtends, 
	a.haveCode, 
	a.approver1, 
	a.approver2, 
	a.approver3, 
	a.approver1Limit, 
	a.approver1LimitRuleId, 
	a.approver1AmonutLower, 
	a.approver1AmonutUpper, 
	a.approver2Limit, 
	a.approver3Limit, 
	a.approveType, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM rolePermission AS a



		AND a.id = #{id}


		AND a.roleId = #{roleId}


		AND a.code = #{code}


		AND a.parentCode = #{parentCode}


		AND a.type = #{type}


		AND a.typeId = #{typeId}


		AND a.typeSys = #{typeSys}


		AND a.haveExtends = #{haveExtends}


		AND a.haveCode = #{haveCode}


		AND a.approver1 = #{approver1}


		AND a.approver2 = #{approver2}


		AND a.approver3 = #{approver3}


		AND a.approver1Limit = #{approver1Limit}


