		AND a.approver1AmonutUpper = #{approver1AmonutUpper}


		AND a.approver2Limit = #{approver2Limit}


		AND a.approver3Limit = #{approver3Limit}


		AND a.approveType = #{approveType}


		AND a.addDate = #{addDate}


