mapper.delete:
DELETE FROM selecter
WHERE
		1=1

		AND	id = #{id}


		AND	quesId = #{quesId}

mapper.select:
SELECT 
	id, 
	questionId, 
	quesId, 
	content, 
	number, 
	answer
FROM selecter


		AND id = #{id}


		AND questionId = #{questionId}


		AND quesId = #{quesId}


		AND content = #{content}


		AND number = #{number}


		AND answer = #{answer}


LIMIT 1

SELECT 
	id, 
	questionId, 
	quesId, 
	content, 
	number, 
	answer
FROM selecter


		AND id = #{id}


		AND questionId = #{questionId}


		AND quesId = #{quesId}


		AND content = #{content}


		AND number = #{number}


		AND answer = #{answer}


		ORDER BY ${sort} ${order}

SELECT COUNT(1) FROM selecter


		AND id = #{id}


		AND questionId = #{questionId}


		AND quesId = #{quesId}


		AND content = #{content}


		AND number = #{number}


		AND answer = #{answer}
