mapper.delete:
DELETE FROM user


		AND id = #{id}


		AND code = #{code}


		AND loginName = #{loginName}


		AND firstName = #{firstName}


		AND lastName = #{lastName}


		AND displayName = #{displayName}


		AND password = #{password}


		AND userId = #{userId}


		AND datetime = #{datetime}


		AND signature = #{signature}


		AND status = #{status}


		AND email = #{email}


		AND language = #{language}


		AND chineseName = #{chineseName}


		AND departmentId = #{departmentId}


		AND positionId = #{positionId}


		AND personalPhoto = #{personalPhoto}


		AND cardNo = #{cardNo}


		AND badgenumber = #{badgenumber}


		AND workGroup = #{workGroup}


		AND workGroupDetail = #{workGroupDetail}


		AND addDate = #{addDate}


		AND addIp = #{addIp}


		AND addUser = #{addUser}


		AND modifyDate = #{modifyDate}


		AND modifyIp = #{modifyIp}


		AND modifyUser = #{modifyUser}

mapper.select:
SELECT 

	a.id, 
	a.code, 
	a.loginName, 
	a.firstName, 
	a.lastName, 
	a.displayName, 
	a.password, 
	a.userId, 
	a.datetime, 
	a.signature, 
	a.status, 
	a.email, 
	a.language, 
	a.chineseName, 
	a.departmentId, 
	a.positionId, 
	a.personalPhoto, 
	a.cardNo, 
	a.badgenumber, 
	a.workGroup, 
	a.workGroupDetail, 
	a.addDate, 
	a.addIp, 
	a.addUser, 
	a.modifyDate, 
	a.modifyIp, 
	a.modifyUser
FROM user AS a



		AND a.id = #{id}


		AND a.code = #{code}


