mapper.select:
SELECT 

	a.id, 
	a.voltage, 
	a.electricity, 
	a.power, 
	a.electricEnergy, 
	a.powerFactor, 
	a.co2, 
	a.frequency, 
	a.serialInterface, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM electricityMeter AS a



		AND a.id = #{id}


		AND a.voltage = #{voltage}


		AND a.electricity = #{electricity}


		AND a.power = #{power}


		AND a.electricEnergy = #{electricEnergy}


		AND a.powerFactor = #{powerFactor}


		AND a.co2 = #{co2}


