mapper.select:
SELECT 

	a.id, 
	a.theme, 
	a.content, 
	a.sender, 
	a.senderName, 
	a.receiver, 
	a.receiverName, 
	a.copyTo, 
	a.email, 
	a.statusSend, 
	a.type, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM emailTemplate AS a



		AND a.id = #{id}


		AND a.theme = #{theme}


		AND a.content = #{content}


		AND a.sender = #{sender}


		AND a.senderName = #{senderName}


