mapper.select:
SELECT 

	a.id, 
	a.idOrderService, 
	a.applyMonth, 
	a.date, 
	a.week, 
	a.month, 
	a.weekOfMonth, 
	a.leaderId, 
	a.leaderName, 
	a.approveState1, 
	a.approveState2, 
	a.approveDate1, 
	a.approveDate2, 
	a.approver1, 
	a.approver2, 
	a.approveState, 
	a.approveDate, 
	a.approverId, 
	a.approverName, 
	a.typeRecord, 
	a.totalAmount, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM orderServiceRecord AS a



		AND a.id = #{id}


		AND a.idOrderService = #{idOrderService}


		AND a.applyMonth = #{applyMonth}


		AND a.date = #{date}


		AND a.week = #{week}


