mapper.delete:
DELETE FROM permission


		AND id = #{id}


		AND name = #{name}


		AND typePermission = #{typePermission}


		AND typeData = #{typeData}


		AND typeSys = #{typeSys}


		AND type = #{type}


		AND code = #{code}


		AND parentCode = #{parentCode}


		AND level = #{level}


		AND bag = #{bag}


		AND clazz = #{clazz}


		AND method = #{method}


		AND parameter = #{parameter}


		AND parameterVal = #{parameterVal}


		AND skipUrl = #{skipUrl}


		AND value1 = #{value1}


		AND value2 = #{value2}


		AND value3 = #{value3}


		AND value4 = #{value4}


		AND remark = #{remark}


		AND addDate = #{addDate}


		AND addUser = #{addUser}


		AND addIp = #{addIp}


		AND modifiedDate = #{modifiedDate}


		AND modifiedUser = #{modifiedUser}


		AND modifiedIp = #{modifiedIp}

mapper.select:
SELECT 

	a.id, 
	a.name, 
	a.typePermission, 
	a.typeData, 
	a.typeSys, 
	a.type, 
	a.code, 
	a.parentCode, 
	a.level, 
	a.bag, 
	a.clazz, 
	a.method, 
	a.parameter, 
	a.parameterVal, 
	a.skipUrl, 
	a.value1, 
	a.value2, 
	a.value3, 
	a.value4, 
	a.remark, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM permission AS a



		AND a.id = #{id}


		AND a.name = #{name}


		AND a.typePermission = #{typePermission}


		AND a.typeData = #{typeData}


		AND a.typeSys = #{typeSys}


		AND a.type = #{type}


		AND a.code = #{code}


		AND a.parentCode = #{parentCode}


		AND a.level = #{level}


		AND a.bag = #{bag}


		AND a.clazz = #{clazz}


		AND a.method = #{method}


		AND a.parameter = #{parameter}


		AND a.parameterVal = #{parameterVal}


		AND a.skipUrl = #{skipUrl}


		AND a.value1 = #{value1}


		AND a.value2 = #{value2}


		AND a.value3 = #{value3}


		AND a.value4 = #{value4}


		AND a.remark = #{remark}


		AND a.addDate = #{addDate}


		AND a.addUser = #{addUser}


		AND a.addIp = #{addIp}


		AND a.modifiedDate = #{modifiedDate}


		AND a.modifiedUser = #{modifiedUser}


		AND a.modifiedIp = #{modifiedIp}




	ORDER BY a.${sort} ${order}

LIMIT 1

