mapper.select:
SELECT 
	id, 
	quesId, 
	content, 
	number, 
	remark, 
	type, 
	answerId
FROM question


		AND id = #{id}


		AND quesId = #{quesId}


		AND content = #{content}


		AND number = #{number}


		AND remark = #{remark}


		AND type = #{type}


		AND answerId = #{answerId}


LIMIT 1

