mapper.select:
SELECT 
	id, 
	title, 
	description, 
	type, 
	status, 
	addDate, 
	addIp, 
	addUser, 
	modifyDate, 
	modifyIp, 
	modifyUser
FROM questionnaire


		AND id = #{id}


		AND title = #{title}


		AND description = #{description}


		AND type = #{type}


		AND status = #{status}


		AND addDate = #{addDate}


		AND addIp = #{addIp}


		AND addUser = #{addUser}


		AND modifyDate = #{modifyDate}


		AND modifyIp = #{modifyIp}


		AND modifyUser = #{modifyUser}


LIMIT 1

