mapper.delete:
DELETE FROM role


		AND id = #{id}


		AND name = #{name}


		AND level = #{level}


		AND description = #{description}


		AND system = #{system}


		AND userId = #{userId}


		AND dateTime = #{dateTime}


		AND status = #{status}


		AND type = #{type}


		AND typeSys = #{typeSys}


		AND typeExtends = #{typeExtends}


		AND typeId = #{typeId}


