		AND addUser = #{addUser}


		AND addIp = #{addIp}


		AND modifiedDate = #{modifiedDate}


		AND modifiedUser = #{modifiedUser}


		AND modifiedIp = #{modifiedIp}

mapper.select:
SELECT 

	a.id, 
	a.name, 
	a.level, 
	a.description, 
	a.system, 
	a.userId, 
	a.dateTime, 
	a.status, 
	a.type, 
	a.typeSys, 
	a.typeExtends, 
	a.typeId, 
	a.addDate, 
	a.addUser, 
	a.addIp, 
	a.modifiedDate, 
	a.modifiedUser, 
	a.modifiedIp
FROM role AS a



		AND a.id = #{id}


		AND a.name = #{name}


		AND a.level = #{level}


		AND a.description = #{description}


		AND a.system = #{system}


		AND a.userId = #{userId}


		AND a.dateTime = #{dateTime}


		AND a.status = #{status}


		AND a.type = #{type}


		AND a.typeSys = #{typeSys}


		AND a.typeExtends = #{typeExtends}


		AND a.typeId = #{typeId}


		AND a.addDate = #{addDate}


		AND a.addUser = #{addUser}


		AND a.addIp = #{addIp}


		AND a.modifiedDate = #{modifiedDate}


		AND a.modifiedUser = #{modifiedUser}


		AND a.modifiedIp = #{modifiedIp}



LIMIT 1

SELECT 

