	addUser, 
	modifyDate, 
	modifyIp, 
	modifyUser
FROM userQuestion


		AND id = #{id}


		AND quesId = #{quesId}


		AND questionId = #{questionId}


		AND result = #{result}


		AND remark = #{remark}


		AND status = #{status}


		AND userId = #{userId}


		AND addDate = #{addDate}


		AND addIp = #{addIp}


		AND addUser = #{addUser}


		AND modifyDate = #{modifyDate}


		AND modifyIp = #{modifyIp}


		AND modifyUser = #{modifyUser}


		ORDER BY ${sort} ${order}

